    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
      
        int s = Integer.parseInt(txtsisi1.getText());
        int dia1 = Integer.parseInt(d1.getText());
        int dia2 = Integer.parseInt(d2.getText());
        double c = Math.sqrt( Math.pow(dia1,2) + Math.pow(dia2,2) );
        double d = Math.sqrt( Math.pow(dia1,3) + Math.pow(dia2,3) );
        
        int lu = 6*s*s;
        int vo = s*s*s;
        
        luas.setText(String.valueOf(lu));
        kll1.setText(String.valueOf(vo));
        
        ds.setText(String.valueOf(c));
        dr.setText(String.valueOf(d));
        
        
    }//GEN-LAST:event_jButton2ActionPerformed

